package Javacontrolsystem;

public class Algebrajava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
double a=2;
double b=4;
double lhs=Math.pow((a+b),2);
double rhs=Math.pow(a,2)+Math.pow(b,2)+2*a*b;
if(lhs==rhs){
	System.out.println("hence proved");}
else{
	System.out.println("hence nott proved");}
	

}

	}


