package PatternPrograms;

public class DiclareingNumerics {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 5;
		for (int i = 1; i <= a; i++) {
			for (int k = 1; k <= i; k++) {
				System.out.print(k+" ");
			}
			System.out.println();
		}
	}

}
